import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {
  email!: string;
  password!: string;
  message: string = ''; // Variável para armazenar uma mensagem a ser exibida no HTML

  constructor(private authService: AuthService, private router: Router) { }
  login() {
    this.authService.login(this.email, this.password).subscribe(
      response => {
        if (response.token) {
          this.message = 'Login bem-sucedido';
          this.router.navigate(['/outra-pagina']);
        } else {
          this.message = 'Login falhou';
        }
      },
      error => {
        //console.error('Erro na solicitação de login:', error);
        this.message = 'Usuário ou senha inválidos';
      }
    );
  }

}
<div class="registration-form">
  <h2>Login</h2>
  <form (ngSubmit)="login()">
    
    <div class="form-group">
      <label for="email">Email:</label>
      <input type="email" id="email" [(ngModel)]="email" name="email" class="form-control" required>
    </div>

    <div class="form-group">
      <label for="password">Senha:</label>
      <input type="password" id="password" [(ngModel)]="password" name="password" class="form-control" required>
    </div>
    <br />
    <button type="submit" class="btn btn-primary">Cadastrar</button>
  </form>
</div>

<p>{{message}}</p>
