import { Component, OnInit } from '@angular/core';
import * as L from 'leaflet';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-leaflet-map',
  templateUrl: './leaflet-map.component.html',
  styleUrls: ['./leaflet-map.component.css']
})
export class LeafletMapComponent implements OnInit {
  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    const map = L.map('map').setView([-18.924067, -48.282142], 12);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(map);

    // Carregue o arquivo JSON localmente da pasta "assets"
    this.http.get('assets/positions.json')
      .subscribe((positionsData: any) => {
        const positions = positionsData;

        const markers: L.Layer[] = [];
        const polygon = L.polyline([], { color: 'blue' }).addTo(map);

        positions.forEach(function (position: { latitude: string; longitude: string; }) {
          const lat = parseFloat(position.latitude);
          const lon = parseFloat(position.longitude);
          const marker = L.marker([lat, lon]).addTo(map);
          markers.push(marker);
          polygon.addLatLng([lat, lon]);
        });

        map.fitBounds(L.featureGroup(markers).getBounds());
        map.addLayer(polygon);
      });
  }
}
