import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { environment } from '../../environments/environment';

@Component({
  selector: 'app-user-registration',
  templateUrl: './user-registration.component.html',
  styleUrls: ['./user-registration.component.css']
})
export class UserRegistrationComponent {
  public newUser: User = new User(); // Adicionamos um novo usuário para o registro
  message: string = '';
  apiUrl: string = environment.apiUrl;
  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string, private router: Router) { }

  // Adicionamos um método para enviar o novo usuário ao servidor
  onSubmit() {
    this.apiUrl =+ 'api/Users';
    this.http.post(this.apiUrl, this.newUser).subscribe(
      response => {
        console.log('Usuário cadastrado com sucesso', response);
        this.router.navigate(['/login']);
      },
      error => {
        this.message = 'Erro ao salvar usuario.';
        console.error('Erro ao cadastrar usuário', error);
      }
    );
  }
}

class User {
  name: string = '';
  email: string = '';
  password: string = '';
  role: string = '';
  phone: string = '';
  cpf: string = '';
}
<div class="registration-form">
  <h2>Cadastro de Usuário</h2>
  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="name">Nome:</label>
      <input type="text" id="name" [(ngModel)]="newUser.name" name="name" class="form-control" required>
    </div>

    <div class="form-group">
      <label for="email">Email:</label>
      <input type="email" id="email" [(ngModel)]="newUser.email" name="email" class="form-control" required>
    </div>

    <div class="form-group">
      <label for="password">Senha:</label>
      <input type="password" id="password" [(ngModel)]="newUser.password" name="password" class="form-control" required>
    </div>

    
    <div class="form-group">
      <label for="phone">Telefone:</label>
      <input type="tel" id="phone" [(ngModel)]="newUser.phone" name="phone" class="form-control">
    </div>

    <div class="form-group">
      <label for="cpf">CPF:</label>
      <input type="text" id="cpf" [(ngModel)]="newUser.cpf" name="cpf" class="form-control">
    </div>
    <br>
    <button type="submit" class="btn btn-primary">Cadastrar</button>
  </form>
  <p class="error-message" style="color: red; font-weight: bold; font-size: 16px;">{{message}}</p>
</div>
