import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  public token: string | undefined;

  constructor(private http: HttpClient) { }

  login(email: string, password: string) {
    return this.http.post<any>('https://localhost:7003/api/Users/login?email=joao.silva@example.com&password=senha123', { email, password }).subscribe(response => {
      if (response.token) {
        var token = response.token;
        // Salve o token em localStorage ou em algum local seguro.
        localStorage.setItem('token', token);
      }
    });
  }

  getToken() {
    // Recupere o token de localStorage ou de onde você o armazenou.
    var token = localStorage.getItem('token');
    return token;
  }

  logout() {
    // Remova o token quando o usuário sair.
    localStorage.removeItem('token');
  }
}
