import { Component, OnInit } from '@angular/core';
import * as L from 'leaflet';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-leaflet-map',
  templateUrl: './leaflet-map.component.html',
  styleUrls: ['./leaflet-map.component.css']
})
export class LeafletMapComponent implements OnInit {
  totalDistance: number = 0;
  map!: L.Map;
  markers: L.Layer[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.map = L.map('map').setView([-18.924067, -48.282142], 12);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(this.map);

    this.http.get('./assets/positions.json').subscribe((data: any) => {
      const positionsData = data;
      const positions = positionsData.data;
      const polygon = L.polyline([], { color: 'blue' }).addTo(this.map);

      let prevPosition: any = null;

      positions.forEach((position: any) => {
        const lat = parseFloat(position.latitude);
        const lon = parseFloat(position.longitude);
        const marker = L.marker([lat, lon]).addTo(this.map);
        this.markers.push(marker);

        if (prevPosition) {
          const currentLatLng = L.latLng(lat, lon);
          const prevLatLng = L.latLng(prevPosition.latitude, prevPosition.longitude);
          const distance = currentLatLng.distanceTo(prevLatLng);
          this.totalDistance += distance;
        }

        polygon.addLatLng([lat, lon]);
        prevPosition = position;
      });

      this.map.fitBounds(L.featureGroup(this.markers).getBounds());
    });
  }

  addMarker() {
    // Solicite ao usuário que clique no mapa para adicionar um novo marcador
    this.map.on('click', (e: L.LeafletMouseEvent) => {
      const marker = L.marker(e.latlng).addTo(this.map);
      this.markers.push(marker);
      // Recalcule a soma da distância
      this.recalculateTotalDistance();
    });
  }

  recalculateTotalDistance() {
    // Recalcule a soma da distância com base nos marcadores existentes
    this.totalDistance = 0;
    for (let i = 1; i < this.markers.length; i++) {
      const currentLatLng = this.markers[i].g(); // Usando Marker
      const prevLatLng = this.markers[i - 1].getLatLng(); // Usando Marker
      const distance = currentLatLng.distanceTo(prevLatLng);
      this.totalDistance += distance;
    }
  }

}
